                init: function () {
                    this.on('success', function (file, response) {
                        myDropzone.removeFile(file);
                    });

                    this.on('error', function (file, error) {
                        myDropzone.removeFile(file);
                    });
                },
                url: "/admin/media",
                method: "POST",
                maxFilesize: 10,
                paramName: 'media',
                headers: {
                    'X-XSRF-TOKEN': token
                }
